INSERT INTO reviews (movie_id, user_id, text)
VALUES
    ((select m.id from movies m where m.name_russian = 'Побег из Шоушенка'), (SELECT u.id FROM users u WHERE u.nickname = 'Дарлин Эдвардс'), 'Гениальное кино! Смотришь и думаешь «Так не бывает!», но позже понимаешь, что только так и должно быть. Начинаешь заново осмысливать значение фразы, которую постоянно используешь в своей жизни, «Надежда умирает последней». Ведь если ты не надеешься, то все в твоей жизни гаснет, не остается смысла. Фильм наполнен бесконечным числом правильных афоризмов. Я уверена, что буду пересматривать его сотни раз.'),
    ((select m.id from movies m where m.name_russian = 'Побег из Шоушенка'), (SELECT u.id FROM users u WHERE u.nickname = 'Габриэль Джексон'), 'Кино это является, безусловно, «со знаком качества». Что же до первого места в рейтинге, то, думаю, здесь имело место быть выставление «десяточек» от большинства зрителей вкупе с раздутыми восторженными откликами кинокритиков. Фильм атмосферный. Он драматичный. И, конечно, заслуживает того, чтобы находиться довольно высоко в мировом кинематографе.'),
    ((select m.id from movies m where m.name_russian = 'Зеленая миля'), (SELECT u.id FROM users u WHERE u.nickname = 'Рональд Рейнольдс'), 'Перестал удивляться тому, что этот фильм занимает сплошь первые места во всевозможных кино рейтингах. Особенно я люблю когда при экранизации литературного произведение из него в силу специфики кинематографа исчезает ирония и появляется некий сверхреализм, обязанный удерживать зрителя у экрана каждую отдельно взятую секунду.'),
    ((select m.id from movies m where m.name_russian = 'Форрест Гамп'), (SELECT u.id FROM users u WHERE u.nickname = 'Нил Паркер'), 'Много еще можно сказать об этом шедевре. И то, что он учит верить, и то, чтобы никогда не сдаваться… Но самый главный девиз я увидел вот в этой фразе: «Занимайся жизнью, или занимайся смертью».'),
    ((select m.id from movies m where m.name_russian = 'Список Шиндлера'), (SELECT u.id FROM users u WHERE u.nickname = 'Трэвис Райт'), 'Очень хороший фильм, необычный сюжет, я бы даже сказала непредсказуемый. Такие фильмы уже стали редкостью.'),
    ((select m.id from movies m where m.name_russian = '1+1'), (SELECT u.id FROM users u WHERE u.nickname = 'Джесси Паттерсон'), 'У меня не найдётся слов, чтобы описать этот фильм. Не хочется быть банальной и говорить какой он отличный, непредсказуемый и т. д., но от этого никуда не деться к сожалению — ведь он ШЕДЕВРАЛЬНЫЙ!'),
    ((select m.id from movies m where m.name_russian = '1+1'), (SELECT u.id FROM users u WHERE u.nickname = 'Амелия Кэннеди'), 'Скажу сразу — фильм выглядел многообещающе, даже если не брать в расчет что он находился в топе-250 лучших фильмов. Известные голливудские актеры на главных ролях. Но нет в этом фильме должной атмосферы. Нет такого чувства что вот сейчас случится что-то страшное. Что герои попали в ситуацию из которой не смогут выбраться. В общем, не понравилось.'),
    ((select m.id from movies m where m.name_russian = 'Жизнь прекрасна'), (SELECT u.id FROM users u WHERE u.nickname = 'Габриэль Джексон'), 'Все должно быть супер! Супер! Су-пер!» И это именно супер, ну слов других не подберешь.'),
    ((select m.id from movies m where m.name_russian = 'Бойцовский клуб'), (SELECT u.id FROM users u WHERE u.nickname = 'Деннис Крейг'), 'Фильм очень красивый. Не во всем, конечно, но яркие персонажи и костюмы — это уже кое-что.'),
    ((select m.id from movies m where m.name_russian = 'Как приручить дракона'), (SELECT u.id FROM users u WHERE u.nickname = 'Габриэль Джексон'), 'Этот фильм из разряда тех, что могут обеспечить хороший отдых и приподнятое настроение за счёт своей лёгкости, совсем непошлого юмора, умеренной дозы напряжения, динамики нужных скоростей.'),
    ((select m.id from movies m where m.name_russian = 'Как приручить дракона'), (SELECT u.id FROM users u WHERE u.nickname = 'Нил Паркер'), 'Назначается Киношедевром среди развлекательных фильмов.'),
    ((select m.id from movies m where m.name_russian = 'Гладиатор'), (SELECT u.id FROM users u WHERE u.nickname = 'Амелия Кэннеди'), 'Данный кинофильм — нестареющая классика мирового кинематографа, который можно пересматривать до бесконечности и, что удивительно, он не может надоесть.'),
    ((select m.id from movies m where m.name_russian = 'Темный рыцарь'), (SELECT u.id FROM users u WHERE u.nickname = 'Дэрил Брайант'), 'Рекомендую смотреть всем и не обращать внимания на надоевшее уже спасение целого мира одним человеком.'),
    ((select m.id from movies m where m.name_russian = 'Большой куш'), (SELECT u.id FROM users u WHERE u.nickname = 'Дэрил Брайант'), 'Удивлен. Никто не отозвался плохо? Неужели было создано произведение искусства, которое нравится всем, и которое совершенно? Нет. Может, я один такой? Фильм не вызывает во мне никаких эмоций. Неплохая сказочка. Замечательная наивная атмосфера. Местами есть забавные шутки. И, как мне показалось, этот фильм — своего рода стёб над другими боевиками. При этом превосходящий многие боевики.'),
    ((select m.id from movies m where m.name_russian = 'Унесённые призраками'), (SELECT u.id FROM users u WHERE u.nickname = 'Деннис Крейг'), 'Необыкновенно позитивный фильм. Его можно пересматривать много раз для поднятия настроения, находя много смешных, незаметных на первый взгляд моментов.'),
    ((select m.id from movies m where m.name_russian = 'Звёздные войны: Эпизод 4 – Новая надежда'), (SELECT u.id FROM users u WHERE u.nickname = 'Амелия Кэннеди'), 'Легендарный. Культовый. Бессмертный. Три слова. Всего лишь три. А сколько же они выражают неподдельных эмоций и радостных впечатлений по отношению к очередному любимому и уважаемому фильму из минувшего в лету детства? Много. Слишком много. И описать эти сердечные и гарцующие в здравом рассудке чувства обыкновенными строчными предложениями иногда не представляется возможным.'),
    ((select m.id from movies m where m.name_russian = 'Большой куш'), (SELECT u.id FROM users u WHERE u.nickname = 'Трэвис Райт'), 'Приятного просмотра для всех, кто не видел ещё этого шедевра больше впечатлений для тех, кто пересматривает в надцатый раз. =)'),
    ((select m.id from movies m where m.name_russian = 'Большой куш'), (SELECT u.id FROM users u WHERE u.nickname = 'Нил Паркер'), 'Это один из любимых моих фильмов с самого детства. Я видела его столько раз, что знаю практически наизусть. И могу сказать с уверенностью, что посмотрю еще не один раз.'),
    ((select m.id from movies m where m.name_russian = 'Молчание ягнят'), (SELECT u.id FROM users u WHERE u.nickname = 'Габриэль Джексон'), 'Фильм, безусловно, посмотрела уже большая часть населения, которая хоть каким-то образом имеет отношение к кинематографу. Я считаю, что фильм можно пересмотреть еще не один раз.'),
    ((select m.id from movies m where m.name_russian = 'Как приручить дракона'), (SELECT u.id FROM users u WHERE u.nickname = 'Айда Дэвис'), 'Фильм продуман до мельчайших деталей. Идеальный фильм для улучшения настроения, единственный в своем роде. Обязателен к просмотру!'),
    ((select m.id from movies m where m.name_russian = 'Блеф'), (SELECT u.id FROM users u WHERE u.nickname = 'Нил Паркер'), 'Фильм потрясающий, в нем хватает абсолютно всего: и драк, и музыки, и юмора, и любви.'),
    ((select m.id from movies m where m.name_russian = 'Гран Торино'), (SELECT u.id FROM users u WHERE u.nickname = 'Рональд Рейнольдс'), 'У фильма есть свои мелкие недостатки и неточности, но многочисленные достоинства в несколько раз перевешивают. Много вдохновляющего креатива!'),
    ((select m.id from movies m where m.name_russian = 'Молчание ягнят'), (SELECT u.id FROM users u WHERE u.nickname = 'Дарлин Эдвардс'), 'Хоть и не по возрасту мне заводить скрипучую пластинку с мелодией «Раньше и деревья были выше, и трава зеленее…», а хочется. Выражать свою любовь к настолько близкому произведению крайне сложно.'),
    ((select m.id from movies m where m.name_russian = 'Блеф'), (SELECT u.id FROM users u WHERE u.nickname = 'Айда Дэвис'), 'Вердикт: прекрасная, нестареющая классика, которая рекомендована мною для всех.'),
    ((select m.id from movies m where m.name_russian = 'Хороший, плохой, злой'), (SELECT u.id FROM users u WHERE u.nickname = 'Дэрил Брайант'), 'Для воскресного вечернего просмотра подходит по всем критериям.'),
    ((select m.id from movies m where m.name_russian = 'Укрощение строптивого'), (SELECT u.id FROM users u WHERE u.nickname = 'Нил Паркер'), 'Хороший и по-настоящему интересный фильм, с хорошим сюжетом и неплохим музыкальным сопровождением. Всем советую к просмотру.'),
    ((select m.id from movies m where m.name_russian = 'Джанго освобожденный'), (SELECT u.id FROM users u WHERE u.nickname = 'Трэвис Райт'), 'Полагаю, этот фильм должен быть в коллекции каждого уважающего себя киномана.'),
    ((select m.id from movies m where m.name_russian = 'Танцующий с волками'), (SELECT u.id FROM users u WHERE u.nickname = 'Амелия Кэннеди'), 'Нетленный шедевр мирового кинематографа, который можно пересматривать десятки раз и получать все такой — же, извините за выражение, кайф от просмотра. Минусы у фильма, конечно, есть, но черт возьми. Их просто не хочется замечать! Ты настолько поглощен просмотром фильма, что просто не хочется придираться к разным мелочам.'),
    ((select m.id from movies m where m.name_russian = 'Джанго освобожденный'), (SELECT u.id FROM users u WHERE u.nickname = 'Айда Дэвис'), 'Фильм только выигрывает от частого просмотра и всегда поднимает мне настроение (наряду с драмой, тут еще и тонкий юмор).'),
    ((select m.id from movies m where m.name_russian = 'Укрощение строптивого'), (SELECT u.id FROM users u WHERE u.nickname = 'Джесси Паттерсон'), 'Конечно, бесспорно культовый фильм, не реалистичный, наивный, где-то глупый, но такой же увлекательный и удивительный, как и много лет назад.'),
    ((select m.id from movies m where m.name_russian = 'Титаник'), (SELECT u.id FROM users u WHERE u.nickname = 'Джесси Паттерсон'), 'В итоге мы имеем отличный представитель своего жанра, который прошёл проверку временем и до сих пор отлично смотрится, несмотря на классический сюжет.'),
    ((select m.id from movies m where m.name_russian = 'Пролетая над гнездом кукушки'), (SELECT u.id FROM users u WHERE u.nickname = 'Деннис Крейг'), 'Скажу только одно — как я жалею, что не посмотрела его раньше!')
